import { Interval } from './wiget-time-context/widget-time-context.model';
/**
 * Type for describing widget change events. It's possible
 * to extend this by adding additional events to discriminated union type.
 */
export type WidgetChangeEvent = TimeContextEvent;
export interface TimeContextEvent {
    type: 'TIME_CONTEXT';
    data: WidgetTimeContext;
}
export type WidgetChangeEventType = WidgetChangeEvent['type'];
export type DateTimeContext = [Date, Date];
export type WidgetTimeContext = ({
    dateTimeContext: DateTimeContext;
    realtime: boolean;
} & {
    interval?: never;
}) | ({
    dateTimeContext?: never;
} & {
    interval: Interval['id'];
    realtime: boolean;
});
